mapper.select:
SELECT qna_code, qna_title, qna_cont, member_id, qna_date FROM ( SELECT ceil(ROWNUM / #{elementSize}) page, qna_code, qna_title, qna_cont, member_id, qna_date FROM ( SELECT qna_code, qna_title, qna_cont, member_id, to_char(qna_date, 'yyyy-MM-DD HH24:MI:SS') qna_date FROM item_qna
qna_title LIKE '%' || #{search} || '%' OR member_id LIKE '%' || #{search} || '%'
ORDER BY qna_code DESC ) ) WHERE page = #{requestPage}

mapper.insert:
INSERT INTO item_qna_re (
    re_code,
    qna_code,
    re_cont
) VALUES (
             item_qna_re_seq.NEXTVAL,
             #{qnaCode},
             #{reCont}
         )

    SELECT item_qna_re_seq.CURRVAL FROM dual
